    psql "$DATABASE_URL" -f "$BACKUP_FILE"

    echo "$LOG_PREFIX âœ… Base de donnÃ©es restaurÃ©e avec succÃ¨s !"
else
    echo "$LOG_PREFIX âŒ Impossible de trouver un fichier de sauvegarde"
    exit 1
