#!/usr/bin/env node

/**
 * Script de configuration automatique de la base de donnÃ©es PostgreSQL
 * RecrÃ©e automatiquement le schÃ©ma et les donnÃ©es de base si nÃ©cessaire
 */

import { exec } from 'child_process';
import { promisify } from 'util';
import fs from 'fs/promises';
import path from 'path';

const execAsync = promisify(exec);

const LOG_PREFIX = 'ğŸ—„ï¸ [DB-Setup]';

// Configuration de la base de donnÃ©es par dÃ©faut
const DB_CONFIG = {
  name: 'pharma_express_ci',
  description: 'Base de donnÃ©es principale pour Pharma Express CI',
  version: '1.0.0',
  lastUpdate: new Date().toISOString()
};

/**
 * VÃ©rifie si la variable DATABASE_URL est configurÃ©e
 */
function checkDatabaseUrl() {
  const dbUrl = process.env.DATABASE_URL;
  if (!dbUrl) {
    console.error(`${LOG_PREFIX} âŒ Variable DATABASE_URL manquante`);
    console.error(`${LOG_PREFIX} ğŸ’¡ Configurez DATABASE_URL dans les secrets Replit`);
    process.exit(1);
  }
  console.log(`${LOG_PREFIX} âœ… DATABASE_URL configurÃ©e`);
  return dbUrl;
}

/**
 * ExÃ©cute les migrations Drizzle pour crÃ©er/mettre Ã  jour le schÃ©ma
 */
async function runMigrations() {
  try {
    console.log(`${LOG_PREFIX} ğŸ“‹ ExÃ©cution des migrations Drizzle...`);

    // Utilise drizzle-kit pour pousser le schÃ©ma vers la base de donnÃ©es
    const { stdout, stderr } = await execAsync('npx drizzle-kit push');

    if (stderr && !stderr.includes('Warning')) {
      console.warn(`${LOG_PREFIX} âš ï¸ Avertissements migrations:`, stderr);
    }

    console.log(`${LOG_PREFIX} âœ… Migrations exÃ©cutÃ©es avec succÃ¨s`);
    console.log(stdout);

    return true;
  } catch (error) {
    console.error(`${LOG_PREFIX} âŒ Erreur lors des migrations:`, error.message);

    // Si les migrations Ã©chouent, essaie de crÃ©er les tables manuellement
    console.log(`${LOG_PREFIX} ğŸ”„ Tentative de crÃ©ation manuelle des tables...`);
    return await createTablesManually();
  }
}

/**
 * CrÃ©e les tables manuellement si les migrations Drizzle Ã©chouent
 */
async function createTablesManually() {
  try {
    // Import du module db pour exÃ©cuter du SQL brut si nÃ©cessaire
    const { db } = await import('../server/db.js');

    console.log(`${LOG_PREFIX} âœ… Connexion Ã  la base de donnÃ©es Ã©tablie`);

    // Les tables seront crÃ©Ã©es automatiquement par Drizzle ORM lors de la premiÃ¨re utilisation
    // Si nÃ©cessaire, on peut ajouter ici du SQL brut pour forcer la crÃ©ation

    return true;
  } catch (error) {
    console.error(`${LOG_PREFIX} âŒ Impossible de crÃ©er les tables:`, error.message);
    return false;
  }
}

/**
 * VÃ©rifie si la base de donnÃ©es contient des donnÃ©es
 */
async function checkExistingData() {
  try {
    const { db } = await import('../server/db.js');
    const { users } = await import('../shared/schema.js');

    // VÃ©rifie s'il y a des utilisateurs dans la base
    const userCount = await db.select().from(users);

    console.log(`${LOG_PREFIX} ğŸ“Š ${userCount.length} utilisateurs trouvÃ©s dans la base`);

    return userCount.length > 0;
  } catch (error) {
    console.warn(`${LOG_PREFIX} âš ï¸ Impossible de vÃ©rifier les donnÃ©es existantes:`, error.message);
    return false;
  }
}

/**
 * Initialise les donnÃ©es de base (admin, pharmacies de test, etc.)
 */
async function seedDatabase() {
  try {
    console.log(`${LOG_PREFIX} ğŸŒ± Initialisation des donnÃ©es de base...`);

    // Import de la classe PostgresStorage qui contient la logique de seed
    const { PostgresStorage } = await import('../server/postgres-storage.js');

    // CrÃ©er une instance et dÃ©clencher le seed
    const storage = new PostgresStorage();

    // Attendre un peu pour que le seed se termine
    await new Promise(resolve => setTimeout(resolve, 2000));

    console.log(`${LOG_PREFIX} âœ… DonnÃ©es de base initialisÃ©es`);
    return true;
  } catch (error) {
    console.error(`${LOG_PREFIX} âŒ Erreur lors du seed:`, error.message);
    return false;
  }
}

/**
 * Sauvegarde la configuration actuelle
 */
async function saveConfiguration() {
  try {
    const configPath = path.join(process.cwd(), '.database-config.json');

    const config = {
      ...DB_CONFIG,
      setupDate: new Date().toISOString(),
      environment: process.env.NODE_ENV || 'development',
      databaseUrl: process.env.DATABASE_URL ? 'configured' : 'missing'
    };

    await fs.writeFile(configPath, JSON.stringify(config, null, 2));
    console.log(`${LOG_PREFIX} ğŸ’¾ Configuration sauvegardÃ©e dans .database-config.json`);
  } catch (error) {
    console.warn(`${LOG_PREFIX} âš ï¸ Impossible de sauvegarder la configuration:`, error.message);
  }
}

/**
 * Fonction principale d'installation de la base de donnÃ©es
 */
async function setupDatabase() {
  console.log(`${LOG_PREFIX} ğŸš€ DÃ©but de la configuration automatique de la base de donnÃ©es`);
  console.log(`${LOG_PREFIX} ğŸ“… ${new Date().toLocaleString('fr-FR')}`);

  try {
    // 1. VÃ©rifier DATABASE_URL
    checkDatabaseUrl();

    // 2. ExÃ©cuter les migrations
    const migrationsOk = await runMigrations();
    if (!migrationsOk) {
      throw new Error('Ã‰chec des migrations');
    }

    // 3. VÃ©rifier les donnÃ©es existantes
    const hasData = await checkExistingData();

    // 4. Initialiser les donnÃ©es si nÃ©cessaire
    if (!hasData) {
      console.log(`${LOG_PREFIX} ğŸ“ Base de donnÃ©es vide, initialisation des donnÃ©es...`);
      await seedDatabase();
    } else {
      console.log(`${LOG_PREFIX} âœ… DonnÃ©es existantes dÃ©tectÃ©es, pas de seed nÃ©cessaire`);
    }

    // 5. Sauvegarder la configuration
    await saveConfiguration();

    console.log(`${LOG_PREFIX} ğŸ‰ Configuration de la base de donnÃ©es terminÃ©e avec succÃ¨s !`);
    console.log(`${LOG_PREFIX} ğŸ’¡ Votre base de donnÃ©es est maintenant prÃªte et sauvegardÃ©e`);

  } catch (error) {
    console.error(`${LOG_PREFIX} ğŸ’¥ Erreur fatale lors de la configuration:`, error.message);
    console.error(`${LOG_PREFIX} ğŸ”§ VÃ©rifiez votre variable DATABASE_URL et rÃ©essayez`);
    process.exit(1);
  }
}

// ExÃ©cution du script si appelÃ© directement
if (import.meta.url === `file://${process.argv[1]}`) {
  setupDatabase();
}

export { setupDatabase, checkDatabaseUrl, runMigrations, seedDatabase };

