  // MÃ©thode pour vÃ©rifier et rejeter les assignations expirÃ©es
  async checkAndRejectExpiredAssignments(): Promise<void> {
    const db = await getDb();
    const threeMinutesAgo = new Date(Date.now() - 3 * 60 * 1000);

    const expiredOrders = await db.select().from(orders).where(
      and(
        eq(orders.status, 'assigned_pending_acceptance'),
        sql`${orders.assignedAt} < ${threeMinutesAgo}`
      )
    );

    for (const order of expiredOrders) {
      await db.update(orders).set({
        status: 'preparing',
        deliveryPersonId: null,
        assignedAt: null,
        updatedAt: new Date()
      }).where(eq(orders.id, order.id));

      // Notifier le livreur que l'assignation a expirÃ©
      if (order.deliveryPersonId) {
        await db.insert(notifications).values({
          userId: order.deliveryPersonId,
          title: 'Assignation expirÃ©e',
          body: `La commande #${order.id.slice(0, 8)} a Ã©tÃ© rÃ©assignÃ©e car vous n'avez pas rÃ©pondu dans les 3 minutes.`,
          type: 'assignment_expired',
          orderId: order.id
        });
      }
    }
  }

  // MÃ©thode pour accepter une livraison assignÃ©e
  async acceptDeliveryAssignment(orderId: string, deliveryPersonId: string): Promise<Order | undefined> {
    const db = await getDb();

    // VÃ©rifier que la commande est bien assignÃ©e Ã  ce livreur et en attente d'acceptation
    const order = await db.select().from(orders).where(
      and(
        eq(orders.id, orderId),
        eq(orders.deliveryPersonId, deliveryPersonId),
        eq(orders.status, 'assigned_pending_acceptance')
      )
    ).limit(1);

    if (!order[0]) {
      return undefined;
    }

    // VÃ©rifier si l'assignation n'a pas expirÃ© (3 minutes)
    const assignedAt = new Date(order[0].assignedAt!);
    const now = new Date();
    const diffMinutes = (now.getTime() - assignedAt.getTime()) / (1000 * 60);

    if (diffMinutes > 3) {
      // ExpirÃ©e, la rÃ©assigner automatiquement
      await this.rejectDeliveryAssignment(orderId, deliveryPersonId, true);
      return undefined;
    }

    // Accepter la livraison
    const result = await db.update(orders).set({
      status: 'in_transit',
      updatedAt: new Date()
    }).where(eq(orders.id, orderId)).returning();

    // Notifier le patient
    if (result[0]) {
      await db.insert(notifications).values({
        userId: result[0].userId,
        title: 'Livreur en route',
        body: `Votre livreur a acceptÃ© la livraison et est en route vers votre adresse.`,
        type: 'delivery_accepted',
        orderId: orderId
      });
    }

    return result[0];
  }

