    if (pharmacy?.phone) {
      const ownerByPhone = await db.select().from(users).where(
        and(
          eq(users.role, 'pharmacien'),
          eq(users.phone, pharmacy.phone)
        )
      ).limit(1);

      if (ownerByPhone.length > 0) {
