  async respondToDeliveryApplication(applicationId: string, action: string, pharmacyId: string | undefined): Promise<any | null> {
    const user = this.users.get(applicationId);
    if (!user || user.role !== 'livreur' || user.deliveryApplicationStatus !== 'pending') {
      console.log(`âŒ Cannot respond to application: user not found or invalid status`, {
        userId: applicationId,
        userExists: !!user,
        userRole: user?.role,
        currentStatus: user?.deliveryApplicationStatus
      });
      return null;
    }

    console.log(`ğŸ“ Processing delivery application response:`, {
      userId: applicationId,
      userName: `${user.firstName} ${user.lastName}`,
      action,
      pharmacyId,
      currentPharmacyId: user.pharmacyId,
      appliedPharmacyId: user.appliedPharmacyId
    });

    if (action === 'approve') {
      user.deliveryApplicationStatus = 'approved';
      user.pharmacyId = pharmacyId; // Assign the livreur to the pharmacy
      user.appliedPharmacyId = undefined; // Clear the applied pharmacy ID

      console.log(`âœ… Approved delivery application for ${user.firstName} ${user.lastName}`, {
        newPharmacyId: user.pharmacyId,
        newStatus: user.deliveryApplicationStatus
      });

      // Create a notification for the livreur
      await this.createNotification({
        userId: user.id,
        title: 'Candidature acceptÃ©e !',
        body: 'FÃ©licitations ! Votre candidature a Ã©tÃ© acceptÃ©e. Vous pouvez maintenant accÃ©der Ã  votre tableau de bord.',
        type: 'delivery_application_response',
        isRead: false,
      });
    } else { // action === 'reject'
      user.deliveryApplicationStatus = 'rejected';
      user.appliedPharmacyId = undefined; // Clear the applied pharmacy ID
      user.pharmacyId = undefined; // Remove pharmacy assignment

      console.log(`âŒ Rejected delivery application for ${user.firstName} ${user.lastName}`);

      // Create a notification for the livreur
      await this.createNotification({
        userId: user.id,
        title: 'Candidature non retenue',
        body: 'Votre candidature n\'a pas Ã©tÃ© retenue cette fois. Vous pouvez postuler Ã  une autre pharmacie.',
        type: 'delivery_application_response',
        isRead: false,
      });
    }

    this.users.set(applicationId, user);
    console.log(`ğŸ’¾ Updated user in storage:`, {
      userId: user.id,
      pharmacyId: user.pharmacyId,
      deliveryApplicationStatus: user.deliveryApplicationStatus
    });

    return user;
  }

  // Notification operations
  async getUserNotifications(userId: string): Promise<Notification[]> {
    return Array.from(this.notifications.values())
      .filter(n => n.userId === userId)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async createNotification(notification: InsertNotification): Promise<Notification> {
    const id = randomUUID();
    const newNotification: Notification = {
      id,
      ...notification,
      createdAt: new Date(),
    };

    this.notifications.set(id, newNotification);
    return newNotification;
  }

  async markNotificationAsRead(id: string): Promise<Notification | undefined> {
    const notification = this.notifications.get(id);
    if (!notification) return undefined;

    notification.isRead = true;
    this.notifications.set(id, notification);
    return notification;
  }

  // New method to get pharmacist orders
  async getPharmacistOrders(pharmacyId: string): Promise<any[]> {
    const orders = Array.from(this.orders.values()).filter(order => 
      order.pharmacyId === pharmacyId
    );

    // Enrich with user and pharmacy data
    return orders.map(order => ({
      ...order,
      user: this.users.get(order.userId),
      pharmacy: this.pharmacies.get(order.pharmacyId)
    }));
  }

  // New method to get all pharmacist orders (fallback)
  async getAllPharmacistOrders(): Promise<any[]> {
    const orders = Array.from(this.orders.values());

    // Enrich with user and pharmacy data
    return orders.map(order => ({
      ...order,
      user: this.users.get(order.userId),
      pharmacy: this.pharmacies.get(order.pharmacyId)
    }));
  }

  // New method to get all prescriptions
  async getAllPrescriptions(): Promise<any[]> {
    const prescriptions = Array.from(this.prescriptions.values());

    // Enrich with user data
    return prescriptions.map(prescription => ({
      ...prescription,
      user: this.users.get(prescription.userId)
    }));
  }

  // MÃ©thodes supplÃ©mentaires pour les pharmaciens
  async getAllPharmacistOrders(): Promise<Order[]> {
    const allOrders = Array.from(this.orders.values())
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());

    // Enrichir avec les informations de la pharmacie et de l'utilisateur
    const enrichedOrders = allOrders.map(order => {
      const pharmacy = this.pharmacies.get(order.pharmacyId);
      const user = this.users.get(order.userId);
      return {
        ...order,
        pharmacy: pharmacy ? {
          id: pharmacy.id,
          name: pharmacy.name,
          address: pharmacy.address,
          phone: pharmacy.phone,
          rating: pharmacy.rating
        } : null,
        patient: user ? {
          firstName: user.firstName,
          lastName: user.lastName,
          phone: user.phone
        } : null,
        totalAmount: order.totalAmount || '0'
      };
    });

    return enrichedOrders;
  }

  async getPharmacistOrders(pharmacyId: string): Promise<Order[]> {
    const pharmacyOrders = Array.from(this.orders.values())
      .filter(order => order.pharmacyId === pharmacyId)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());

    // Enrichir avec les informations de l'utilisateur
    const enrichedOrders = pharmacyOrders.map(order => {
      const user = this.users.get(order.userId);
      const pharmacy = this.pharmacies.get(order.pharmacyId);
      return {
        ...order,
        pharmacy: pharmacy ? {
          id: pharmacy.id,
          name: pharmacy.name,
          address: pharmacy.address,
          phone: pharmacy.phone,
          rating: pharmacy.rating
        } : null,
        patient: user ? {
          firstName: user.firstName,
          lastName: user.lastName,
          phone: user.phone
        } : null,
        totalAmount: order.totalAmount || '0'
      };
    });

    return enrichedOrders;
  }

  async getAllPrescriptions(): Promise<Prescription[]> {
    return Array.from(this.prescriptions.values())
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async getOrderById(orderId: string): Promise<Order | undefined> {
    const order = this.orders.get(orderId);
    if (!order) return undefined;

    // Enrichir avec les informations de la pharmacie
    const pharmacy = this.pharmacies.get(order.pharmacyId);
    return {
      ...order,
      pharmacy: pharmacy ? {
        id: pharmacy.id,
        name: pharmacy.name,
        address: pharmacy.address,
        phone: pharmacy.phone,
        rating: pharmacy.rating
      } : null,
      totalAmount: order.totalAmount || '0',
      patientId: order.userId // Ajouter un alias pour la compatibilitÃ©
    };
  }

  async updateOrderStatus(id: string, status: string, totalAmount?: number): Promise<Order | undefined> {
    const order = this.orders.get(id);
    if (!order) return undefined;

    order.status = status as any;
    order.updatedAt = new Date();

    if (totalAmount !== undefined) {
      order.totalAmount = totalAmount.toString();
    }

    if (status === 'delivered') {
      order.deliveredAt = new Date();
    }

    this.orders.set(id, order);

    // Retourner l'ordre enrichi
    const pharmacy = this.pharmacies.get(order.pharmacyId);
    return {
      ...order,
      pharmacy: pharmacy ? {
        id: pharmacy.id,
        name: pharmacy.name,
        address: pharmacy.address,
        phone: pharmacy.phone,
        rating: pharmacy.rating
      } : null,
      totalAmount: order.totalAmount || '0'
    };
  }

  async getDeliveryOrders(): Promise<Order[]> {
    const deliveryOrders = Array.from(this.orders.values())
      .filter(order => ['ready_for_delivery', 'in_transit', 'delivered'].includes(order.status))
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());

    // Enrichir avec les informations de la pharmacie et de l'utilisateur
    const enrichedOrders = deliveryOrders.map(order => {
      const pharmacy = this.pharmacies.get(order.pharmacyId);
      const user = this.users.get(order.userId);
      return {
        ...order,
        pharmacy: pharmacy ? {
          id: pharmacy.id,
          name: pharmacy.name,
          address: pharmacy.address,
          phone: pharmacy.phone,
          rating: pharmacy.rating
        } : null,
        patient: user ? {
          firstName: user.firstName,
          lastName: user.lastName,
          phone: user.phone
        } : null,
        totalAmount: order.totalAmount || '0'
      };
    });

    return enrichedOrders;
  }

  async assignDeliveryPerson(orderId: string, deliveryPersonId: string): Promise<Order | undefined> {
    const order = this.orders.get(orderId);
    if (!order) return undefined;

    order.deliveryPersonId = deliveryPersonId;
    // Keep status as 'ready_for_delivery' when assigned, change to 'in_delivery' when delivery starts
    order.updatedAt = new Date();

    // Set assignment time to track expiration
    order.assignedAt = new Date();
    // Set status to 'assigned_pending_acceptance'
    order.status = 'assigned_pending_acceptance';


    this.orders.set(orderId, order);

    // CrÃ©er des notifications pour le livreur et le patient
    const deliveryPerson = this.users.get(deliveryPersonId);
    const patient = this.users.get(order.userId);
    const pharmacy = this.pharmacies.get(order.pharmacyId);

    if (deliveryPerson) {
      // Notification pour le livreur
      await this.createNotification({
        userId: deliveryPersonId,
        title: 'Nouvelle livraison assignÃ©e',
        body: `Commande #${order.id.slice(0, 8)} - ${order.deliveryAddress}`,
        type: 'delivery_assigned',
        orderId: orderId,
        isRead: false
      });
    }

    if (patient) {
      // Notification pour le patient
      await this.createNotification({
        userId: order.userId,
        title: 'Livreur assignÃ© Ã  votre commande',
        message: `${deliveryPerson?.firstName} ${deliveryPerson?.lastName} va livrer votre commande. Contact: ${deliveryPerson?.phone}`,
        type: 'delivery_assigned',
        isRead: false
      });
    }

    // Retourner l'ordre enrichi
    return {
      ...order,
      pharmacy: pharmacy ? {
        id: pharmacy.id,
        name: pharmacy.name,
        address: pharmacy.address,
        phone: pharmacy.phone,
        rating: pharmacy.rating
      } : null,
      patient: patient ? {
        firstName: patient.firstName,
        lastName: patient.lastName,
        phone: patient.phone
      } : null,
      deliveryPerson: deliveryPerson ? {
        firstName: deliveryPerson.firstName,
        lastName: deliveryPerson.lastName,
        phone: deliveryPerson.phone
      } : null,
      totalAmount: order.totalAmount || '0'
    };
  }

  async getMyAssignedOrders(deliveryPersonId: string): Promise<Order[]> {
    // Nettoyer les assignations expirÃ©es
    await this.cleanupExpiredAssignments();

    // RÃ©cupÃ©rer les commandes assignÃ©es Ã  ce livreur
    const assignedOrders = Array.from(this.orders.values()).filter(order => 
      order.deliveryPersonId === deliveryPersonId && 
      ['assigned_pending_acceptance', 'in_transit', 'delivered'].includes(order.status)
    ).sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());

    // Enrichir avec les donnÃ©es de pharmacie et patient
    return assignedOrders.map(order => {
      const pharmacy = order.pharmacyId ? this.pharmacies.get(order.pharmacyId) : null;
      const patient = this.users.get(order.userId);

      return {
        ...order,
        pharmacy,
        patient
      };
    });
  }

  async acceptDeliveryAssignment(orderId: string, deliveryPersonId: string): Promise<Order | undefined> {
    const order = this.orders.get(orderId);
    if (!order || order.deliveryPersonId !== deliveryPersonId || order.status !== 'assigned_pending_acceptance') {
      return undefined;
    }

    // VÃ©rifier si l'assignation n'a pas expirÃ© (3 minutes)
    if (order.assignedAt) {
      const assignedTime = new Date(order.assignedAt).getTime();
      const now = new Date().getTime();
      const diffMinutes = (now - assignedTime) / (1000 * 60);

      if (diffMinutes > 3) {
        // ExpirÃ©e, rÃ©jecter automatiquement
        await this.rejectDeliveryAssignment(orderId, deliveryPersonId, true);
        return undefined;
      }
    }

    // Accepter la livraison
    order.status = 'in_transit';
    order.updatedAt = new Date();
    this.orders.set(orderId, order);

    // Notifier le patient
    await this.createNotification({
      userId: order.userId,
      title: 'Livreur en route',
      body: `Votre livreur a acceptÃ© la livraison et est en route vers votre adresse.`,
      type: 'delivery_accepted',
      orderId: orderId,
      isRead: false
    });

    return order;
  }

  async rejectDeliveryAssignment(orderId: string, deliveryPersonId: string, isExpired: boolean = false): Promise<boolean> {
    const order = this.orders.get(orderId);
    if (!order || order.deliveryPersonId !== deliveryPersonId || order.status !== 'assigned_pending_acceptance') {
      return false;
    }

    // RÃ©assigner la commande (retourner en statut ready_for_delivery car elle Ã©tait dÃ©jÃ  prÃªte)
    order.status = 'ready_for_delivery';
    order.deliveryPersonId = undefined;
    order.assignedAt = undefined;
    order.updatedAt = new Date();
    this.orders.set(orderId, order);

    // Notifier selon le type de rejet
    if (!isExpired) {
      await this.createNotification({
        userId: deliveryPersonId,
        title: 'Livraison refusÃ©e',
        body: `Vous avez refusÃ© la commande #${orderId.slice(0, 8)}. Elle sera rÃ©assignÃ©e Ã  un autre livreur.`,
        type: 'delivery_rejected',
        orderId: orderId,
        isRead: false
      });
    }

    return true;
  }

  async cleanupExpiredAssignments(): Promise<void> {
    const threeMinutesAgo = new Date(Date.now() - 3 * 60 * 1000);

    const expiredOrders = Array.from(this.orders.values()).filter(order => 
      order.status === 'assigned_pending_acceptance' && 
      order.assignedAt && 
      new Date(order.assignedAt) < threeMinutesAgo
    );

    for (const order of expiredOrders) {
      const expiredDeliveryPersonId = order.deliveryPersonId; // Sauvegarder l'ID avant de le supprimer

      // Retourner au statut ready_for_delivery (pas preparing) car la commande Ã©tait dÃ©jÃ  prÃªte
      order.status = 'ready_for_delivery';
      order.deliveryPersonId = undefined;
      order.assignedAt = undefined;
      order.updatedAt = new Date();
      this.orders.set(order.id, order);

      // Notifier le livreur que l'assignation a expirÃ©
      if (expiredDeliveryPersonId) {
        await this.createNotification({
          userId: expiredDeliveryPersonId,
          title: 'Assignation expirÃ©e',
          body: `La commande #${order.id.slice(0, 8)} a Ã©tÃ© rÃ©assignÃ©e car vous n'avez pas rÃ©pondu dans les 3 minutes.`,
          type: 'assignment_expired',
          orderId: order.id,
          isRead: false
        });
      }
    }
  }



  async confirmDeliveryCompletion(orderId: string): Promise<Order | undefined> {
    const order = this.orders.get(orderId);
    if (!order) return undefined;

    // Accepter plusieurs statuts pour la confirmation de livraison
    if (!['arrived_pending_confirmation', 'in_transit', 'in_delivery'].includes(order.status)) {
      return undefined;
    }

    // Marquer la livraison comme terminÃ©e avec confirmation directe du patient
    order.status = 'delivered';
    order.patientConfirmedAt = new Date();
    order.deliveredAt = new Date();
    order.updatedAt = new Date();
    this.orders.set(orderId, order);

    // Notifier le livreur que la livraison est confirmÃ©e
    if (order.deliveryPersonId) {
      await this.createNotification({
        userId: order.deliveryPersonId,
        title: 'Livraison confirmÃ©e',
        body: 'Le patient a confirmÃ© la rÃ©ception. Livraison terminÃ©e avec succÃ¨s !',
        type: 'delivery_completed',
        orderId: orderId,
        isRead: false
      });
    }

    return order;
  }

  async deleteOrder(id: string): Promise<boolean> {
    const deleted = this.orders.delete(id);
    console.log(`Order ${id} ${deleted ? 'successfully deleted' : 'not found'} from memory storage`);
    return deleted;
  }

  async cleanupOldOrders(): Promise<number> {
    const now = new Date();
    const twentyFourHoursAgo = new Date(now.getTime() - 24 * 60 * 60 * 1000);
    const fiveDaysAgo = new Date(now.getTime() - 5 * 24 * 60 * 60 * 1000);

    let deletedCount = 0;
    const ordersToDelete: string[] = [];

    // Parcourir toutes les commandes
    for (const [orderId, order] of this.orders.entries()) {
      const orderDate = new Date(order.createdAt);

      // Supprimer si:
      // - Plus de 24h et statut != 'delivered' 
      // - Plus de 5 jours et statut == 'delivered'
      if (order.status !== 'delivered' && orderDate < twentyFourHoursAgo) {
        ordersToDelete.push(orderId);
      } else if (order.status === 'delivered' && orderDate < fiveDaysAgo) {
        ordersToDelete.push(orderId);
      }
    }

    // Supprimer les commandes identifiÃ©es
    for (const orderId of ordersToDelete) {
      this.orders.delete(orderId);
      deletedCount++;
    }

    if (deletedCount > 0) {
      console.log(`ğŸ—‘ï¸ Nettoyage automatique: ${deletedCount} commandes supprimÃ©es`);
    }

    return deletedCount;
  }

  // Password reset operations (in-memory storage)
  private passwordResetCodes: Map<string, PasswordResetCode> = new Map();

  async createPasswordResetCode(resetCode: InsertPasswordResetCode): Promise<PasswordResetCode> {
    const id = randomUUID();
    const newResetCode: PasswordResetCode = {
      id,
      ...resetCode,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.passwordResetCodes.set(id, newResetCode);
    return newResetCode;
  }

  async getValidPasswordResetCode(email: string, code: string): Promise<PasswordResetCode | undefined> {
    const now = new Date();
    return Array.from(this.passwordResetCodes.values()).find(resetCode => 
      resetCode.email === email &&
      resetCode.code === code &&
      !resetCode.used &&
      resetCode.expiresAt > now
    );
  }

  async markPasswordResetCodeUsed(id: string): Promise<void> {
    const resetCode = this.passwordResetCodes.get(id);
    if (resetCode) {
      resetCode.used = true;
      resetCode.updatedAt = new Date();
      this.passwordResetCodes.set(id, resetCode);
    }
  }
}

