    queryFn: async () => {
      console.log('ğŸ”„ Fetching delivery applications...');
      const response = await fetch("/api/pharmacien/delivery-applications", {
        credentials: 'include'
      });
      if (!response.ok) {
        const errorText = await response.text();
        console.error('âŒ Failed to fetch applications:', response.status, errorText);
        throw new Error(`Failed to fetch applications: ${response.status}`);
      }
      const data = await response.json();
      console.log('ğŸ“¬ Applications received:', data);
      return data;
    },
    refetchInterval: 10000, // Refresh every 10 seconds
    retry: 3
  });

  const respondToApplicationMutation = useMutation({
    mutationFn: ({ applicationId, action }: { applicationId: string; action: string }) =>
      apiRequest(`/api/pharmacien/delivery-applications/${applicationId}/respond`, "POST", { action }),
    onSuccess: (data, variables) => {
      toast({
        title: variables.action === 'approve' ? "Candidature acceptÃ©e" : "Candidature rejetÃ©e",
