                                if (order.totalAmount && order.totalAmount !== '0' && order.totalAmount !== '0.00') {
                                  return `${parseFloat(order.totalAmount).toFixed(0)} FCFA`;
                                }
                                // Calculer le total Ã  partir des mÃ©dicaments si disponible
                                try {
                                  const medications = typeof order.medications === 'string' ? JSON.parse(order.medications) : (order.medications || []);
                                  const total = medications.reduce((sum: number, med: any) => {
                                    const price = parseFloat(med.price) || 0;
                                    const isAvailable = med.available !== false;
                                    return sum + (price > 0 && isAvailable ? price : 0);
                                  }, 0);
                                  return total > 0 ? `${total.toFixed(0)} FCFA` : 'En cours d\'Ã©valuation';
                                } catch (error) {
                                  console.error('Error calculating total:', error);
                                  return 'En cours d\'Ã©valuation';
                                }
                              })()} 
                            </div>
                            <div className="text-sm">
                              <span className="font-medium">Adresse:</span> {order.deliveryAddress}
                            </div>
                          </div>

                          <div className="flex space-x-2">
                            <Button
                              size="sm"
                              className="bg-blue-600 hover:bg-blue-700"
                              onClick={() => handleOrderUpdate(order.id, 'ready_for_delivery')}
                              disabled={updateOrderMutation.isPending}
                            >
                              ğŸ“¦ PrÃªt pour livraison
                            </Button>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Section: Commandes prÃªtes pour livraison avec assignation de livreur */}
                  <div>
                    <h3 className="font-semibold text-lg mb-3 flex items-center">
                      <span className="bg-green-100 rounded-full w-8 h-8 flex items-center justify-center mr-2">ğŸ“¦</span>
                      PrÃªtes pour Livraison - Assignation des Livreurs
                    </h3>
                    <ReadyForDeliveryOrders orders={orders || []} />
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>

        {/* Dialog d'authentification pour l'accÃ¨s aux paramÃ¨tres */}
        <Dialog open={showPasswordDialog} onOpenChange={setShowPasswordDialog}>
          <DialogContent className="sm:max-w-md">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                ğŸ” Authentification requise
              </DialogTitle>
              <DialogDescription>
                Pour accÃ©der aux paramÃ¨tres de gestion de la pharmacie, veuillez confirmer votre identitÃ© en saisissant votre mot de passe.
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="security-password">Mot de passe</Label>
                <Input
                  id="security-password"
                  type="password"
                  placeholder="Saisissez votre mot de passe"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  onKeyPress={(e) => {
                    if (e.key === 'Enter' && !isVerifying) {
                      handlePasswordConfirmation();
                    }
                  }}
                  disabled={isVerifying}
                />
              </div>

              <div className="flex justify-end space-x-2">
                <Button
                  variant="outline"
                  onClick={() => {
                    setShowPasswordDialog(false);
                    setPassword("");
                    setPendingAction(null);
                  }}
                  disabled={isVerifying}
                >
                  Annuler
                </Button>
                <Button
                  onClick={handlePasswordConfirmation}
                  disabled={isVerifying || !password.trim()}
                  className="bg-green-600 hover:bg-green-700"
                >
                  {isVerifying ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      VÃ©rification...
                    </>
                  ) : (
                    "Confirmer"
                  )}
                </Button>
              </div>
            </div>

            <div className="bg-blue-50 border border-blue-200 rounded-lg p-3 mt-4">
              <p className="text-sm text-blue-800">
                <strong>SÃ©curitÃ© :</strong> Cette vÃ©rification permet de s'assurer que seul le titulaire du compte peut accÃ©der aux paramÃ¨tres sensibles de la pharmacie.
              </p>
            </div>
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

