import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { useGeolocation } from "@/hooks/use-geolocation";

const addressSchema = z.object({
  street: z.string().min(1, "L'adresse est requise"),
  city: z.string().min(1, "La ville est requise"),
  postalCode: z.string().min(1, "Le code postal est requis"),
  country: z.string().min(1, "Le pays est requis"),
  details: z.string().optional(),
});

type AddressData = z.infer<typeof addressSchema>;

export default function DeliveryAddress() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { latitude, longitude, error: geoError, loading: geoLoading, refetch } = useGeolocation();
  const [addressFromLocation, setAddressFromLocation] = useState<string>("");
  const [isReverseGeocoding, setIsReverseGeocoding] = useState(false);

  const {
    register,
    handleSubmit,
    setValue,
    watch,
    formState: { errors, isSubmitting }
  } = useForm<AddressData>({
    resolver: zodResolver(addressSchema),
    defaultValues: {
      street: "",
      city: "",
      postalCode: "",
      country: "CÃ´te d'Ivoire",
      details: "",
    },
  });

  // Reverse geocoding pour obtenir l'adresse depuis les coordonnÃ©es
  useEffect(() => {
    if (latitude && longitude && !isReverseGeocoding) {
      setIsReverseGeocoding(true);

      // Utiliser un service de gÃ©ocodage inverse (ici on simule)
      const reverseGeocode = async () => {
        try {
          // En production, vous utiliseriez un service comme Google Maps API
          // Pour cette dÃ©mo, on simule une adresse
          setTimeout(() => {
            const mockAddress = "Rue des Pharmacies, Abidjan";
            setAddressFromLocation(mockAddress);
            setValue("street", mockAddress);
            setValue("city", "Abidjan");
            setValue("postalCode", "00225");
            setIsReverseGeocoding(false);

            toast({
              title: "Position dÃ©tectÃ©e",
              description: "Votre adresse a Ã©tÃ© automatiquement remplie. Vous pouvez la modifier si nÃ©cessaire.",
            });
          }, 2000);
        } catch (error) {
          console.error('Erreur gÃ©ocodage inverse:', error);
          setIsReverseGeocoding(false);
        }
      };

      reverseGeocode();
    }
  }, [latitude, longitude, setValue, toast]);

  const handleGetLocation = () => {
    refetch();
  };

  const onSubmit = (data: AddressData) => {
    // Sauvegarder l'adresse
    localStorage.setItem('deliveryAddress', JSON.stringify({
      ...data,
      coordinates: latitude && longitude ? { latitude, longitude } : null
    }));

    toast({
      title: "Adresse sauvegardÃ©e",
      description: "Votre adresse de livraison a Ã©tÃ© enregistrÃ©e avec succÃ¨s.",
    });

    setLocation("/profile");
  };

  const goBack = () => {
    setLocation("/profile");
  };

  return (
    <div className="min-h-screen bg-pharma-bg">
      {/* Header */}
      <header className="bg-white px-4 py-3 shadow-sm sticky top-0 z-10">
        <div className="flex items-center space-x-3">
          <Button
            variant="ghost"
            size="icon"
            onClick={goBack}
            className="w-10 h-10"
          >
            <svg className="w-5 h-5 text-gray-600" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
          </Button>
          <h1 className="text-xl font-bold text-gray-900">
            Adresse de livraison
          </h1>
        </div>
      </header>

      <div className="px-4 py-6">
        {/* Geolocation Card */}
        <Card className="shadow-sm mb-6">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <svg className="w-5 h-5 text-pharma-green" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M5.05 4.05a7 7 0 119.9 9.9L10 18.9l-4.95-4.95a7 7 0 010-9.9zM10 11a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd" />
              </svg>
              <span>Localisation automatique</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            {geoError && (
              <div className="bg-red-50 border border-red-200 rounded-lg p-3 mb-4">
                <p className="text-red-600 text-sm">{geoError}</p>
              </div>
            )}

            <Button
              onClick={handleGetLocation}
              disabled={geoLoading || isReverseGeocoding}
              className="w-full bg-pharma-green hover:bg-pharma-green/90"
            >
              {geoLoading || isReverseGeocoding ? (
                <>
                  <div className="w-4 h-4 mr-2 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                  {isReverseGeocoding ? 'RÃ©cupÃ©ration de l\'adresse...' : 'Localisation en cours...'}
                </>
              ) : (
                <>
                  <svg className="w-4 h-4 mr-2" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M5.05 4.05a7 7 0 119.9 9.9L10 18.9l-4.95-4.95a7 7 0 010-9.9zM10 11a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd" />
                  </svg>
                  Utiliser ma position actuelle
                </>
              )}
            </Button>

            {latitude && longitude && (
              <div className="mt-3 p-3 bg-green-50 border border-green-200 rounded-lg">
                <p className="text-green-700 text-sm">
                  ğŸ“ Position dÃ©tectÃ©e: {latitude.toFixed(6)}, {longitude.toFixed(6)}
                </p>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Address Form */}
        <Card className="shadow-sm">
          <CardHeader>
            <CardTitle>Informations d'adresse</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
              <div>
                <Label htmlFor="street">Adresse complÃ¨te</Label>
                <Input
                  id="street"
                  {...register("street")}
                  placeholder="NumÃ©ro, rue, quartier"
                />
                {errors.street && (
                  <p className="text-red-500 text-sm mt-1">{errors.street.message}</p>
                )}
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="city">Ville</Label>
                  <Input
                    id="city"
                    {...register("city")}
                    placeholder="Votre ville"
                  />
                  {errors.city && (
                    <p className="text-red-500 text-sm mt-1">{errors.city.message}</p>
                  )}
                </div>
                <div>
                  <Label htmlFor="postalCode">Code postal</Label>
                  <Input
                    id="postalCode"
                    {...register("postalCode")}
                    placeholder="Code postal"
                  />
                  {errors.postalCode && (
                    <p className="text-red-500 text-sm mt-1">{errors.postalCode.message}</p>
                  )}
                </div>
              </div>

              <div>
                <Label htmlFor="country">Pays</Label>
                <Input
                  id="country"
                  {...register("country")}
                  placeholder="Votre pays"
                />
                {errors.country && (
                  <p className="text-red-500 text-sm mt-1">{errors.country.message}</p>
                )}
              </div>

              <div>
                <Label htmlFor="details">DÃ©tails supplÃ©mentaires (optionnel)</Label>
                <Textarea
                  id="details"
                  {...register("details")}
                  placeholder="Ã‰tage, appartement, point de repÃ¨re..."
                  className="min-h-[80px]"
                />
              </div>

              <div className="flex gap-3 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={goBack}
                  className="flex-1"
                >
                  Annuler
                </Button>
                <Button
                  type="submit"
                  disabled={isSubmitting}
                  className="flex-1 bg-pharma-green hover:bg-pharma-green/90"
                >
                  {isSubmitting ? (
                    <>
                      <div className="w-4 h-4 mr-2 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                      Sauvegarde...
                    </>
                  ) : (
                    'Sauvegarder l\'adresse'
                  )}
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/delivery-application.ts.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ArrowLeft, Upload, FileText, Briefcase } from "lucide-react";

const applicationSchema = z.object({
  pharmacyId: z.string().min(1, "ID de pharmacie requis"),
  motivationLetter: z.string().min(50, "La lettre de motivation doit contenir au moins 50 caractÃ¨res"),
  experience: z.string().min(10, "Veuillez dÃ©crire votre expÃ©rience"),
  availability: z.string().min(5, "Veuillez indiquer vos disponibilitÃ©s"),
  phone: z.string().min(10, "NumÃ©ro de tÃ©lÃ©phone requis"),
  idDocument: z.any().optional(),
  drivingLicense: z.any().optional(),
  cvDocument: z.any().optional(),
});

type ApplicationForm = z.infer<typeof applicationSchema>;

export default function DeliveryApplication() {
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Get pharmacy from localStorage (passed from pharmacies page)
  const selectedPharmacy = JSON.parse(localStorage.getItem('selectedPharmacyForApplication') || '{}');

  const form = useForm<ApplicationForm>({
    resolver: zodResolver(applicationSchema),
    defaultValues: {
      pharmacyId: selectedPharmacy.id || '',
      motivationLetter: '',
      experience: '',
      availability: '',
      phone: '',
    },
  });

  const applicationMutation = useMutation({
    mutationFn: async (data: ApplicationForm) => {
      const formData = new FormData();
      formData.append('pharmacyId', data.pharmacyId);
      formData.append('motivationLetter', data.motivationLetter);
      formData.append('experience', data.experience);
      formData.append('availability', data.availability);
      formData.append('phone', data.phone);

      if (data.idDocument?.[0]) {
        formData.append('idDocument', data.idDocument[0]);
      }
      if (data.drivingLicense?.[0]) {
        formData.append('drivingLicense', data.drivingLicense[0]);
      }
      if (data.cvDocument?.[0]) {
        formData.append('cvDocument', data.cvDocument[0]);
      }

      const response = await fetch('/api/delivery/apply', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!response.ok) {
        const text = (await response.text()) || response.statusText;
        throw new Error(`${response.status}: ${text}`);
      }

      return await response.json();
    },
    onSuccess: () => {
      toast({
        title: "Candidature envoyÃ©e âœ…",
        description: "Votre candidature a Ã©tÃ© envoyÃ©e avec succÃ¨s. La pharmacie vous contactera bientÃ´t.",
      });
      localStorage.removeItem('selectedPharmacyForApplication');
      queryClient.invalidateQueries({ queryKey: ['/api/auth/user'] });
      setLocation('/pharmacies');
    },
    onError: (error: any) => {
      toast({
        title: "Erreur d'envoi",
        description: error.message || "Une erreur est survenue lors de l'envoi de votre candidature.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ApplicationForm) => {
    setIsSubmitting(true);
    applicationMutation.mutate(data);
  };

  if (!selectedPharmacy.id) {
    return (
      <div className="min-h-screen bg-gray-50 p-4">
        <div className="max-w-md mx-auto pt-8">
          <Card>
            <CardContent className="p-6 text-center">
              <h3 className="font-semibold text-gray-900 mb-2">Pharmacie non sÃ©lectionnÃ©e</h3>
              <p className="text-gray-600 mb-4">Vous devez sÃ©lectionner une pharmacie depuis la liste pour postuler.</p>
              <Button onClick={() => setLocation('/pharmacies')}>
                Retour aux pharmacies
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 p-4">
        <div className="max-w-md mx-auto flex items-center space-x-3">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation('/pharmacies')}
            data-testid="button-back"
          >
            <ArrowLeft className="h-5 w-5" />
          </Button>
          <div className="flex-1">
            <h1 className="text-lg font-semibold text-gray-900">Candidature livreur</h1>
            <p className="text-sm text-gray-600">{selectedPharmacy.name}</p>
          </div>
        </div>
      </div>

      <div className="max-w-md mx-auto p-4 space-y-4">
        {/* Pharmacy Info */}
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-3">
              <div className="w-12 h-12 bg-pharma-green rounded-full flex items-center justify-center">
                <Briefcase className="h-6 w-6 text-white" />
              </div>
              <div className="flex-1">
                <h3 className="font-semibold text-gray-900">{selectedPharmacy.name}</h3>
                <p className="text-sm text-gray-600">{selectedPharmacy.address}</p>
                <p className="text-xs text-pharma-green font-medium">Recherche livreur</p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Application Form */}
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Formulaire de candidature</CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                {/* Lettre de motivation */}
                <FormField
                  control={form.control}
                  name="motivationLetter"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Lettre de motivation *</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Expliquez pourquoi vous souhaitez travailler pour cette pharmacie..."
                          className="min-h-[100px]"
                          {...field}
                          data-testid="textarea-motivation"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* ExpÃ©rience */}
                <FormField
                  control={form.control}
                  name="experience"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>ExpÃ©rience en livraison *</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="DÃ©crivez votre expÃ©rience en livraison (moto, scooter, etc.)..."
                          {...field}
                          data-testid="textarea-experience"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* DisponibilitÃ©s */}
                <FormField
                  control={form.control}
                  name="availability"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>DisponibilitÃ©s *</FormLabel>
                      <FormControl>
                        <Input
                          placeholder="Ex: Lundi-Vendredi 8h-18h, Weekend disponible"
                          {...field}
                          data-testid="input-availability"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* TÃ©lÃ©phone */}
                <FormField
                  control={form.control}
                  name="phone"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>TÃ©lÃ©phone de contact *</FormLabel>
                      <FormControl>
                        <Input
                          placeholder="+225 XX XX XX XX"
                          {...field}
                          data-testid="input-phone"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Documents */}
                <div className="space-y-3">
                  <h4 className="font-medium text-gray-900">Documents Ã  joindre</h4>

                  <FormField
                    control={form.control}
                    name="idDocument"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center space-x-2">
                          <FileText className="h-4 w-4" />
                          <span>Carte d'identitÃ© *</span>
                        </FormLabel>
                        <FormControl>
                          <Input
                            type="file"
                            accept="image/*,.pdf"
                            onChange={(e) => field.onChange(e.target.files)}
                            data-testid="input-id-document"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="drivingLicense"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center space-x-2">
                          <FileText className="h-4 w-4" />
                          <span>Permis de conduire *</span>
                        </FormLabel>
                        <FormControl>
                          <Input
                            type="file"
                            accept="image/*,.pdf"
                            onChange={(e) => field.onChange(e.target.files)}
                            data-testid="input-driving-license"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="cvDocument"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center space-x-2">
                          <Upload className="h-4 w-4" />
                          <span>CV (optionnel)</span>
                        </FormLabel>
                        <FormControl>
                          <Input
                            type="file"
                            accept="image/*,.pdf,.doc,.docx,application/pdf,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
                            onChange={(e) => field.onChange(e.target.files)}
                            data-testid="input-cv"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Submit Button */}
                <Button 
                  type="submit" 
                  className="w-full bg-pharma-green hover:bg-pharma-green/90" 
                  disabled={isSubmitting || applicationMutation.isPending}
                  data-testid="button-submit-application"
                >
                  {isSubmitting || applicationMutation.isPending ? (
                    <div className="flex items-center space-x-2">
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                      <span>Envoi en cours...</span>
                    </div>
                  ) : (
                    <div className="flex items-center space-x-2">
                      <Upload className="h-4 w-4" />
                      <span>Envoyer la candidature</span>
                    </div>
                  )}
                </Button>
              </form>
            </Form>
          </CardContent>
        </Card>

        <div className="text-center text-xs text-gray-500 pb-8">
          Votre candidature sera examinÃ©e par la pharmacie sous 24-48h
        </div>
      </div>
    </div>
  );
}

