import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useGeolocation } from "@/hooks/use-geolocation";
import BottomNavigation from "@/components/bottom-navigation";
import { useLocation } from "wouter";
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix Leaflet default markers
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

export default function DeliveryMapLivreur() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();
  const { latitude, longitude } = useGeolocation();
  const [map, setMap] = useState<L.Map | null>(null);
  const [selectedDelivery, setSelectedDelivery] = useState<any>(null);
  const [routeInfo, setRouteInfo] = useState<{distance: number, duration: number} | null>(null);
  const mapRef = useRef<HTMLDivElement>(null);
  const deliveryMarkersRef = useRef<L.Marker[]>([]);
  const currentLocationMarkerRef = useRef<L.Marker | null>(null);
  const routePolylineRef = useRef<L.Polyline | null>(null);

  // Configuration des tuiles OSM
  const OSM_TILE_URL = 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png';
  const OSM_ATTRIBUTION = 'Â© <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors';
  const ABIDJAN_CENTER = { lat: 5.3364, lng: -4.0267 };

  // ğŸš€ Ã‰tats GPS haute frÃ©quence - Style Google Maps
  const [isGPSActive, setIsGPSActive] = useState(false);
  const [lastGPSUpdate, setLastGPSUpdate] = useState<Date | null>(null);
  const [currentSpeed, setCurrentSpeed] = useState(0);
  const [currentBearing, setBearing] = useState(0);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const lastPositionRef = useRef<{lat: number, lng: number, timestamp: number} | null>(null);

  // Configuration tracking GPS professionnelle
  const GPS_CONFIG = {
    TRACKING_INTERVAL: 2000, // 2 secondes comme Google Maps
    HIGH_ACCURACY: true,
    TIMEOUT: 5000, // 5 secondes timeout
    MAX_AGE: 1000, // Cache max 1 seconde
    MINIMUM_DISTANCE: 5, // 5 mÃ¨tres minimum pour mise Ã  jour
  };

  // RÃ©cupÃ©rer les livraisons du livreur
  const { data: myDeliveries } = useQuery({
    queryKey: ["/api/livreur/deliveries"],
    enabled: true,
    refetchInterval: 10000,
  });

  // ğŸ“¡ Mutation GPS haute prÃ©cision avec nouveaux champs
  const updateGPSLocationMutation = useMutation({
    mutationFn: async ({ lat, lng, speed, bearing, accuracy, timestamp }: {
      lat: number;
      lng: number;
      speed?: number;
      bearing?: number;
      accuracy?: number;
      timestamp?: string;
    }) => {
      if (!user?.id) throw new Error('Utilisateur non connectÃ©');

      return apiRequest(`/api/delivery-persons/${user.id}/location`, 'POST', {
        lat,
        lng,
        speed: speed || 0,
        bearing: bearing || 0,
        accuracy: accuracy || 5,
        timestamp: timestamp || new Date().toISOString()
      });
    },
    onSuccess: () => {
      setLastGPSUpdate(new Date());
    },
    onError: (error) => {
      console.error('âŒ Erreur mise Ã  jour GPS:', error);
    }
  });

  // Fonction pour calculer la route rÃ©elle
  const calculateRoute = async (startLat: number, startLng: number, endLat: number, endLng: number) => {
    try {
      const response = await fetch(
        `https://router.project-osrm.org/route/v1/driving/${startLng},${startLat};${endLng},${endLat}?overview=full&geometries=geojson`
      );

      if (!response.ok) throw new Error('Erreur de routage');

      const data = await response.json();

      if (data.routes && data.routes.length > 0) {
        const route = data.routes[0];
        const coordinates = route.geometry.coordinates.map((coord: number[]) => [coord[1], coord[0]]);
        const distance = Math.round((route.distance / 1000) * 10) / 10;
        const duration = Math.round(route.duration / 60);

        return { coordinates, distance, duration };
      }
    } catch (error) {
      console.error('Erreur lors du calcul de la route:', error);
      return {
        coordinates: [[startLat, startLng], [endLat, endLng]],
        distance: Math.round(calculateDistance(startLat, startLng, endLat, endLng) * 10) / 10,
        duration: Math.round(calculateDistance(startLat, startLng, endLat, endLng) * 3)
      };
    }
  };

  const calculateDistance = (lat1: number, lng1: number, lat2: number, lng2: number) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLng = (lng2 - lng1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLng/2) * Math.sin(dLng/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  // ğŸ§® Calcul de la vitesse et direction en temps rÃ©el
  const calculateMovementData = (newLat: number, newLng: number, currentTime: number) => {
    if (!lastPositionRef.current) {
      lastPositionRef.current = { lat: newLat, lng: newLng, timestamp: currentTime };
      return { speed: 0, bearing: 0 };
    }

    const lastPos = lastPositionRef.current;
    const timeDelta = (currentTime - lastPos.timestamp) / 1000; // secondes
    const distance = calculateDistance(lastPos.lat, lastPos.lng, newLat, newLng) * 1000; // mÃ¨tres

    // Calculer vitesse (km/h)
    const speed = timeDelta > 0 ? (distance / timeDelta) * 3.6 : 0;

    // Calculer direction/bearing (degrÃ©s)
    const dLng = (newLng - lastPos.lng) * Math.PI / 180;
    const lat1 = lastPos.lat * Math.PI / 180;
    const lat2 = newLat * Math.PI / 180;

    const y = Math.sin(dLng) * Math.cos(lat2);
    const x = Math.cos(lat1) * Math.sin(lat2) - Math.sin(lat1) * Math.cos(lat2) * Math.cos(dLng);
    let bearing = Math.atan2(y, x) * 180 / Math.PI;
    bearing = (bearing + 360) % 360; // Normaliser 0-360Â°

    // Mettre Ã  jour derniÃ¨re position
    lastPositionRef.current = { lat: newLat, lng: newLng, timestamp: currentTime };

    return { speed: Math.max(0, speed), bearing };
  };

  // ğŸš€ DÃ©marrage/ArrÃªt automatique du tracking GPS haute frÃ©quence
  useEffect(() => {
    if (!user?.id) return;

    // DÃ©marrer tracking si livraisons actives
    const hasActiveDeliveries = myDeliveries?.some((d: any) => 
      ['assigned_pending_acceptance', 'in_transit', 'preparing', 'ready_for_delivery'].includes(d.status)
    );

    if (hasActiveDeliveries && !isGPSActive) {
      console.log('ğŸš€ DÃ©marrage tracking GPS haute frÃ©quence (2s)');
      startHighFrequencyGPSTracking();
    } else if (!hasActiveDeliveries && isGPSActive) {
      console.log('â¹ï¸ ArrÃªt tracking GPS - plus de livraisons actives');
      stopGPSTracking();
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [myDeliveries, user?.id]);

  // ğŸ“ Fonction de tracking GPS haute prÃ©cision
  const startHighFrequencyGPSTracking = () => {
    if (isGPSActive || !navigator.geolocation) return;

    setIsGPSActive(true);

    const sendGPSUpdate = () => {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude, accuracy, speed: gpsSpeed } = position.coords;
          const timestamp = Date.now();

          // Calculer mouvement si position prÃ©cÃ©dente disponible
          const { speed: calculatedSpeed, bearing } = calculateMovementData(latitude, longitude, timestamp);

          // Utiliser vitesse GPS si disponible, sinon vitesse calculÃ©e
          const finalSpeed = gpsSpeed !== null && gpsSpeed > 0 ? gpsSpeed * 3.6 : calculatedSpeed; // km/h

          // Mettre Ã  jour les Ã©tats
          setCurrentSpeed(finalSpeed);
          setBearing(bearing);

          // Envoyer via nouvelle API WebSocket
          updateGPSLocationMutation.mutate({
            lat: latitude,
            lng: longitude,
            speed: finalSpeed,
            bearing,
            accuracy: accuracy || 5,
            timestamp: new Date(timestamp).toISOString()
          });

          if (import.meta.env.DEV) {
            console.log('ğŸ“ GPS haute frÃ©quence envoyÃ©:', {
              lat: latitude.toFixed(6),
              lng: longitude.toFixed(6),
              speed: finalSpeed.toFixed(1) + ' km/h',
              bearing: bearing.toFixed(0) + 'Â°',
              accuracy: accuracy + 'm'
            });
          }
        },
        (error) => {
          console.error('âŒ Erreur GPS:', error);
          // Continuer le tracking mÃªme en cas d'erreur ponctuelle
        },
        {
          enableHighAccuracy: GPS_CONFIG.HIGH_ACCURACY,
          timeout: GPS_CONFIG.TIMEOUT,
          maximumAge: GPS_CONFIG.MAX_AGE
        }
      );
    };

    // Premier envoi immÃ©diat
    sendGPSUpdate();

    // Puis toutes les 2 secondes
    intervalRef.current = setInterval(sendGPSUpdate, GPS_CONFIG.TRACKING_INTERVAL);

    toast({
      title: "ğŸš€ Tracking GPS activÃ©",
      description: "Position envoyÃ©e toutes les 2s en temps rÃ©el",
    });
  };

  // â¹ï¸ ArrÃªt du tracking GPS
  const stopGPSTracking = () => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }
    setIsGPSActive(false);
    setCurrentSpeed(0);
    setBearing(0);
    setLastGPSUpdate(null);
    lastPositionRef.current = null;

    toast({
      title: "â¹ï¸ Tracking GPS dÃ©sactivÃ©",
      description: "Plus de livraisons actives",
    });
  };

  // Initialize map
  useEffect(() => {
    if (!mapRef.current) return;

    // DÃ©lai pour s'assurer que le DOM est prÃªt et Ã©viter les conflits HMR
    const initTimer = setTimeout(() => {
      if (!mapRef.current) return;

      try {
        const initialMap = L.map(mapRef.current, {
          center: [ABIDJAN_CENTER.lat, ABIDJAN_CENTER.lng],
          zoom: 12,
          zoomControl: true,
        });

        L.tileLayer(OSM_TILE_URL, {
          attribution: OSM_ATTRIBUTION,
          maxZoom: 19,
        }).addTo(initialMap);

        setMap(initialMap);
        console.log('âœ… Carte Leaflet initialisÃ©e');
      } catch (error) {
        console.error('âŒ Erreur initialisation carte:', error);
      }
    }, 200);

    return () => {
      clearTimeout(initTimer);
      if (map) {
        try {
          map.remove();
        } catch (error) {
          console.warn('Nettoyage carte:', error);
        }
      }
    };
  }, []);

  // Add current location marker
  useEffect(() => {
    if (!map || !latitude || !longitude) return;

    // VÃ©rifier que la carte est valide et a un conteneur DOM
    try {
      if (!map.getContainer() || !map.getContainer().parentNode) {
        console.warn('Carte Leaflet non valide, attente de rÃ©initialisation...');
        return;
      }
    } catch (error) {
      console.warn('Erreur lors de la vÃ©rification de la carte:', error);
      return;
    }

    // Nettoyer le marqueur existant en toute sÃ©curitÃ©
    if (currentLocationMarkerRef.current) {
      try {
        currentLocationMarkerRef.current.remove();
      } catch (error) {
        console.warn('Erreur lors du nettoyage du marqueur:', error);
      }
      currentLocationMarkerRef.current = null;
    }

    const currentLocationIcon = L.divIcon({
      html: `
        <div style="
          background: #3B82F6; 
          width: 24px; 
          height: 24px; 
          border-radius: 50%; 
          border: 3px solid white; 
          box-shadow: 0 0 0 6px rgba(59, 130, 246, 0.3);
          display: flex;
          align-items: center;
          justify-content: center;
          animation: pulse 2s infinite;
        ">
          <svg width="14" height="14" fill="white" viewBox="0 0 20 20">
            <path fill-rule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clip-rule="evenodd" />
          </svg>
        </div>
        <style>
          @keyframes pulse {
            0% { box-shadow: 0 0 0 6px rgba(59, 130, 246, 0.3); }
            70% { box-shadow: 0 0 0 20px rgba(59, 130, 246, 0); }
            100% { box-shadow: 0 0 0 6px rgba(59, 130, 246, 0); }
          }
        </style>
      `,
      className: 'current-location-marker',
      iconSize: [24, 24],
      iconAnchor: [12, 12],
    });

    // DÃ©lai additionnel pour s'assurer que la carte est prÃªte
    const addMarkerTimer = setTimeout(() => {
      try {
        if (!map || !map.getContainer() || !map.getContainer().parentNode) {
          console.warn('Carte non prÃªte pour les marqueurs');
          return;
        }

        currentLocationMarkerRef.current = L.marker([latitude, longitude], { icon: currentLocationIcon })
          .addTo(map)
          .bindPopup('ğŸ“ Votre position actuelle');

        map.setView([latitude, longitude], 14);
      } catch (error) {
        console.error('Erreur lors de l\'ajout du marqueur de position:', error);
      }
    }, 300);

    return () => clearTimeout(addMarkerTimer);
  }, [map, latitude, longitude]);

  // Add delivery markers
  useEffect(() => {
    if (!map || !myDeliveries) return;

    // VÃ©rifier que la carte est valide
    try {
      if (!map.getContainer() || !map.getContainer().parentNode) {
        console.warn('Carte Leaflet non valide pour les marqueurs de livraison');
        return;
      }
    } catch (error) {
      console.warn('Erreur lors de la vÃ©rification de la carte pour les livraisons:', error);
      return;
    }

    // Clear existing markers safely
    deliveryMarkersRef.current.forEach(marker => {
      try {
        marker.remove();
      } catch (error) {
        console.warn('Erreur lors du nettoyage d\'un marqueur:', error);
      }
    });
    deliveryMarkersRef.current = [];

    const activeDeliveries = myDeliveries.filter((delivery: any) => 
      ['assigned_pending_acceptance', 'in_transit', 'arrived_pending_confirmation'].includes(delivery.status)
    );

    activeDeliveries.forEach((delivery: any, index: number) => {
      // Utiliser les vraies coordonnÃ©es GPS de livraison du patient
      let deliveryLat, deliveryLng;

      if (delivery.deliveryLatitude && delivery.deliveryLongitude) {
        // Utiliser les coordonnÃ©es GPS rÃ©elles et prÃ©cises stockÃ©es dans la commande
        deliveryLat = parseFloat(delivery.deliveryLatitude.toString());
        deliveryLng = parseFloat(delivery.deliveryLongitude.toString());
        console.log(`âœ… Utilisation coordonnÃ©es GPS prÃ©cises pour livraison ${delivery.id}: ${deliveryLat}, ${deliveryLng}`);
      } else {
        // Erreur critique : pas de coordonnÃ©es GPS disponibles
        console.error(`âŒ ERREUR: Pas de coordonnÃ©es GPS pour la livraison ${delivery.id}. L'adresse "${delivery.deliveryAddress}" ne peut pas Ãªtre localisÃ©e prÃ©cisÃ©ment.`);
        toast({
          title: "CoordonnÃ©es manquantes",
          description: `Livraison ${delivery.id.slice(0, 8)}: Pas de coordonnÃ©es GPS prÃ©cises disponibles`,
          variant: "destructive",
        });
        return; // Skip cette livraison
      }

      // CrÃ©er une icÃ´ne simple et efficace pour le point d'arrivÃ©e (destination)
      const colorPrimary = delivery.status === 'in_transit' ? '#F97316' : '#10B981';
      const colorSecondary = delivery.status === 'in_transit' ? '#EA580C' : '#059669';

      const deliveryIcon = L.divIcon({
        html: `
          <div style="
            position: relative;
            width: 30px;
            height: 30px;
            background: ${colorPrimary};
            border: 3px solid white;
            border-radius: 50%;
            box-shadow: 0 3px 8px rgba(0,0,0,0.4);
            display: flex;
            align-items: center;
            justify-content: center;
            cursor: pointer;
          ">
            <svg width="16" height="16" fill="white" viewBox="0 0 24 24">
              <path d="M12 2C8.13 2 5 5.13 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-3.87-3.13-7-7-7zm0 9.5c-1.38 0-2.5-1.12-2.5-2.5s1.12-2.5 2.5-2.5 2.5 1.12 2.5 2.5-1.12 2.5-2.5 2.5z"/>
            </svg>
            <div style="
              position: absolute;
              bottom: -8px;
              left: 50%;
              width: 0;
              height: 0;
              border-left: 6px solid transparent;
              border-right: 6px solid transparent;
              border-top: 8px solid ${colorSecondary};
              transform: translateX(-50%);
            "></div>
          </div>
        `,
        className: 'delivery-marker-icon',
        iconSize: [30, 38],
        iconAnchor: [15, 38],
        popupAnchor: [0, -38]
      });

      try {
        const marker = L.marker([deliveryLat, deliveryLng], { icon: deliveryIcon })
          .addTo(map)
          .bindPopup(`
            <div class="p-3">
              <div class="flex items-center mb-2">
                <span class="text-lg mr-2">ğŸ¯</span>
                <h4 class="font-bold text-blue-800">DESTINATION</h4>
              </div>
              <p class="text-sm font-semibold text-gray-800">Livraison #${delivery.id.slice(0, 8)}</p>
              <p class="text-sm text-gray-600 mt-1">ğŸ“ ${delivery.deliveryAddress}</p>
              <p class="text-sm text-green-600 font-medium mt-1">ğŸ’° ${delivery.totalAmount} FCFA</p>
              <button onclick="window.selectDelivery('${delivery.id}')" class="mt-3 px-4 py-2 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-lg text-sm font-medium shadow-md hover:shadow-lg transition-all">
                ğŸ—ºï¸ Tracer l'itinÃ©raire
              </button>
            </div>
          `);

        // Store delivery coordinates for routing
        (marker as any).deliveryData = {
          ...delivery,
          coordinates: { lat: deliveryLat, lng: deliveryLng }
        };

        deliveryMarkersRef.current.push(marker);
      } catch (error) {
        console.error(`Erreur lors de l'ajout du marqueur livraison ${delivery.id}:`, error);
      }
    });

    // Fit map to show all markers
    if (deliveryMarkersRef.current.length > 0 && currentLocationMarkerRef.current) {
      const group = new L.FeatureGroup([
        currentLocationMarkerRef.current,
        ...deliveryMarkersRef.current
      ]);
      map.fitBounds(group.getBounds().pad(0.1));
    }
  }, [map, myDeliveries]);

  // Global function to select delivery
  useEffect(() => {
    (window as any).selectDelivery = (deliveryId: string) => {
      const delivery = myDeliveries?.find((d: any) => d.id === deliveryId);
      if (delivery) {
        setSelectedDelivery(delivery);
        // Calculer automatiquement l'itinÃ©raire
        setTimeout(() => showRouteToDelivery(delivery), 500);
      }
    };

    return () => {
      delete (window as any).selectDelivery;
    };
  }, [myDeliveries, latitude, longitude]);

  const showRouteToDelivery = async (delivery: any) => {
    if (!latitude || !longitude || !map) {
      toast({
        title: "Position manquante",
        description: "Votre position GPS n'est pas disponible",
        variant: "destructive",
      });
      return;
    }

    // VÃ©rifier la prÃ©sence des coordonnÃ©es GPS prÃ©cises
    if (!delivery.deliveryLatitude || !delivery.deliveryLongitude) {
      toast({
        title: "CoordonnÃ©es GPS manquantes",
        description: `Impossible de calculer l'itinÃ©raire vers ${delivery.deliveryAddress} - coordonnÃ©es GPS non disponibles`,
        variant: "destructive",
      });
      return;
    }

    // Utiliser les coordonnÃ©es GPS rÃ©elles et prÃ©cises
    const deliveryLat = parseFloat(delivery.deliveryLatitude.toString());
    const deliveryLng = parseFloat(delivery.deliveryLongitude.toString());

    console.log(`ğŸ—ºï¸ Calcul itinÃ©raire prÃ©cis:`);
    console.log(`  ğŸ“ Position livreur: ${latitude}, ${longitude}`);
    console.log(`  ğŸ  Position patient: ${deliveryLat}, ${deliveryLng}`);
    console.log(`  ğŸ“§ Adresse: ${delivery.deliveryAddress}`);

    try {
      const route = await calculateRoute(
        latitude, longitude,
        deliveryLat, deliveryLng
      );

      setRouteInfo({
        distance: route.distance,
        duration: route.duration
      });

      // Remove existing route
      if (routePolylineRef.current) {
        routePolylineRef.current.remove();
      }

      // Add new route
      routePolylineRef.current = L.polyline(route.coordinates, {
        color: '#F97316',
        weight: 5,
        opacity: 0.8,
        dashArray: '10, 5'
      }).addTo(map);

      // Fit map to route
      map.fitBounds(routePolylineRef.current.getBounds().pad(0.1));

      toast({
        title: "ItinÃ©raire calculÃ© âœ…",
        description: `Distance: ${route.distance}km â€¢ DurÃ©e: ${route.duration}min â€¢ Destination: ${delivery.deliveryAddress}`,
      });

    } catch (error) {
      // CrÃ©er un itinÃ©raire simple en ligne droite en cas d'erreur
      const simpleRoute = [[latitude, longitude], [deliveryLat, deliveryLng]];
      const distance = calculateDistance(latitude, longitude, deliveryLat, deliveryLng);

      setRouteInfo({
        distance: Math.round(distance * 10) / 10,
        duration: Math.round(distance * 3)
      });

      if (routePolylineRef.current) {
        routePolylineRef.current.remove();
      }

      routePolylineRef.current = L.polyline(simpleRoute, {
        color: '#DC2626',
        weight: 4,
        opacity: 0.7,
        dashArray: '15, 10'
      }).addTo(map);

      map.fitBounds(routePolylineRef.current.getBounds().pad(0.1));

      toast({
        title: "ItinÃ©raire approximatif",
        description: `Distance: ${Math.round(distance * 10) / 10}km (ligne droite) â€¢ Destination: ${delivery.deliveryAddress}`,
      });
    }
  };

  // Mutation to update delivery status
  const updateDeliveryMutation = useMutation({
    mutationFn: ({ orderId, status }: { orderId: string; status: string }) =>
      apiRequest(`/api/livreur/deliveries/${orderId}/status`, "POST", { status }),
    onSuccess: () => {
      toast({
        title: "Statut mis Ã  jour",
        description: "Le statut de la livraison a Ã©tÃ© mis Ã  jour",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/livreur/deliveries"] });
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Impossible de mettre Ã  jour le statut",
        variant: "destructive",
      });
    },
  });

  const handleStartDelivery = () => {
    if (selectedDelivery) {
      updateDeliveryMutation.mutate({ 
        orderId: selectedDelivery.id, 
        status: 'in_transit' 
      });
    }
  };

  const handleArrivalConfirmation = () => {
    if (selectedDelivery) {
