import { createStorage } from "./storage-factory";

export class CleanupService {
  private intervalId: NodeJS.Timeout | null = null;
  private readonly storage = createStorage();

  // Intervalle de nettoyage: toutes les heures (3600000 ms)
  private readonly CLEANUP_INTERVAL = 60 * 60 * 1000; // 1 heure

  start(): void {
    if (this.intervalId) {
      console.log('âš ï¸ Service de nettoyage dÃ©jÃ  dÃ©marrÃ©');
      return;
    }

    console.log('ğŸš€ DÃ©marrage du service de nettoyage automatique des commandes');

    // ExÃ©cuter immÃ©diatement au dÃ©marrage puis toutes les heures
    this.performCleanup();

    this.intervalId = setInterval(() => {
      this.performCleanup();
    }, this.CLEANUP_INTERVAL);

    console.log(`â° Service de nettoyage programmÃ© toutes les ${this.CLEANUP_INTERVAL / (60 * 1000)} minutes`);
  }

  stop(): void {
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = null;
      console.log('ğŸ›‘ Service de nettoyage arrÃªtÃ©');
    }
  }

  private async performCleanup(): Promise<void> {
    try {
      console.log('ğŸ§¹ ExÃ©cution du nettoyage automatique...');
      const deletedCount = await this.storage.cleanupOldOrders();

      if (deletedCount > 0) {
        console.log(`âœ… Nettoyage terminÃ©: ${deletedCount} commandes supprimÃ©es`);
      } else {
        console.log('âœ… Nettoyage terminÃ©: aucune commande Ã  supprimer');
      }
    } catch (error) {
      console.error('âŒ Erreur lors du nettoyage automatique:', error);
    }
  }

  // MÃ©thode pour forcer un nettoyage manuel (utile pour les tests)
  async forceCleanup(): Promise<number> {
    console.log('ğŸ§¹ Nettoyage manuel forcÃ©...');
    return await this.storage.cleanupOldOrders();
  }
}

// Instance singleton du service
export const cleanupService = new CleanupService();

